                            />
                          </NavLink>
                        </Tooltip>
                      </ListItem>
                      <ListItem className={cx({ [classes.item]: true }, { [classes.collapseItem]: true })}>
                        <Tooltip title='ALTA DE DEPARTAMENTO' disableHoverListener={!miniActive}>
                          <NavLink
                            to={'/admin/departament-register'}
