                          </NavLink>
                        </Tooltip>
                      </ListItem>
                      <ListItem className={cx({ [classes.item]: true }, { [classes.collapseItem]: true })}>
                        <Tooltip title='ALTA DE ROL' disableHoverListener={!miniActive}>
                          <NavLink
                            to={'/admin/role-register'}
                            className={`${classes.itemLink}` + ' ' + classes.collapseItemLink}
